import React, { useState, useEffect } from "react";
import { 
  View, 
  Text, 
  StyleSheet, 
  Image, 
  TouchableOpacity, 
  TextInput, 
  ScrollView, 
  SafeAreaView,
  ActivityIndicator,
  Alert
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { LinearGradient } from "expo-linear-gradient";
import { ScreenKey } from "../../App";

// ‚ö†Ô∏è CONFIRME SE A PORTA EST√Å CORRETA (8080 ou 8081)
const API_URL = "http://localhost:8080"; 

type Tab = "match" | "chat" | "profile";

export default function MainApp({ onNavigate }: { onNavigate: (s: ScreenKey) => void }) {
  const [tab, setTab] = useState<Tab>("match");
  const [loading, setLoading] = useState(false);

  // ESTADOS PARA GUARDAR OS DADOS VINDOS DA API
  const [matchData, setMatchData] = useState<any>(null);
  const [myProfile, setMyProfile] = useState<any>(null);

  // Carrega os dados assim que a tela abre
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      // 1. Recuperar Token e ID da mem√≥ria do telem√≥vel
      const token = await AsyncStorage.getItem("userToken");
      const userId = await AsyncStorage.getItem("userId");

      if (!token || !userId) {
        // Se n√£o tiver login salvo, volta para o in√≠cio
        onNavigate("login");
        return;
      }

      // 2. Configurar o Axios com o Token
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // 3. Buscar o MATCH DO DIA
      try {
        const resMatch = await axios.get(`${API_URL}/api/matches/today`);
        console.log("Match encontrado:", resMatch.data);
        setMatchData(resMatch.data);
      } catch (error) {
        console.log("Nenhum match encontrado ou erro:", error);
        setMatchData(null); 
      }

      // 4. Buscar o MEU PERFIL (Para a aba de perfil)
      try {
        const resProfile = await axios.get(`${API_URL}/api/profiles/user/${userId}`);
        setMyProfile(resProfile.data);
      } catch (error) {
        console.log("Erro ao buscar meu perfil:", error);
      }

    } catch (err) {
      console.error("Erro geral na tela principal:", err);
      Alert.alert("Erro", "N√£o foi poss√≠vel carregar os dados.");
    } finally {
      setLoading(false);
    }
  };

  // Fun√ß√£o para Sair (Logout)
  const handleLogout = async () => {
    await AsyncStorage.clear(); // Limpa o token
    onNavigate("welcome");
  };

  // Tela de Carregamento
  if (loading) {
    return (
      <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
        <ActivityIndicator size="large" color="#4f46e5" />
        <Text style={{marginTop: 10}}>A procurar o teu match...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>

        {/* === ABA 1: MATCH === */}
        {tab === "match" && (
          <ScrollView style={styles.page} contentContainerStyle={{flexGrow: 1}}>
            <View style={{ alignItems: "center", marginVertical: 16 }}>
              <Text style={{ color: "#6b7280", fontSize: 14 }}>O teu match expira em</Text>
              <Text style={{ fontSize: 24, fontWeight: "700", color: "#374151" }}>23:59:59</Text>
            </View>

            {matchData ? (
              // SE TIVER MATCH: MOSTRA O CART√ÉO
              <TouchableOpacity style={styles.card} activeOpacity={0.9}>
                <Image 
                  // Tenta pegar a 1¬™ foto. Se n√£o tiver, usa uma padr√£o.
                  source={{ uri: matchData.matchedProfile.photos?.[0]?.url || "https://via.placeholder.com/400" }} 
                  style={styles.cardImage} 
                />
                <LinearGradient
                  colors={["transparent", "rgba(0,0,0,0.9)"]}
                  style={styles.cardInfoContainer}
                >
                  <Text style={styles.cardName}>
                    {matchData.matchedProfile.name}, {new Date().getFullYear() - new Date(matchData.matchedProfile.birthDate).getFullYear()}
                  </Text>
                  <Text style={{color: '#e5e7eb', marginTop: 4, fontSize: 16}}>
                    {matchData.matchedProfile.bio || "Sem biografia."}
                  </Text>

                  <View style={styles.tagRow}>
                    <View style={styles.tag}>
                      <Text style={styles.tagText}>üìç {matchData.matchedProfile.lastLocation || "Portugal"}</Text>
                    </View>
                  </View>
                </LinearGradient>
              </TouchableOpacity>
            ) : (
              // SE N√ÉO TIVER MATCH (Tela Cinza)
              <View style={[styles.card, {backgroundColor: '#374151', justifyContent: 'center', alignItems: 'center'}]}>
                <Text style={{fontSize: 50}}>üò¥</Text>
                <Text style={{color: 'white', fontSize: 18, textAlign: 'center', padding: 20}}>
                  Ainda n√£o encontr√°mos ningu√©m novo por hoje.
                </Text>
                <TouchableOpacity onPress={loadData} style={{marginTop: 20, backgroundColor: 'white', paddingVertical: 10, paddingHorizontal: 20, borderRadius: 8}}>
                  <Text style={{fontWeight: 'bold'}}>Tentar Novamente</Text>
                </TouchableOpacity>
              </View>
            )}
          </ScrollView>
        )}

        {/* === ABA 2: CHAT === */}
        {tab === "chat" && (
          <View style={{ flex: 1, backgroundColor: "#ffffff", justifyContent: 'center', alignItems: 'center', padding: 20 }}>
             <Text style={{fontSize: 40, marginBottom: 10}}>üí¨</Text>
             <Text style={{fontSize: 18, color: '#374151', textAlign: 'center'}}>
               O chat estar√° dispon√≠vel quando tiveres um match ativo!
             </Text>
          </View>
        )}

        {/* === ABA 3: PERFIL === */}
        {tab === "profile" && (
          <ScrollView style={styles.page}>
            <Text style={styles.profileTitle}>Meu Perfil</Text>
            {myProfile ? (
              <View style={styles.profileHeader}>
                <Image 
                  source={{ uri: myProfile.photos?.[0]?.url || "https://github.com/github.png" }} 
                  style={styles.profileAvatar} 
                />
                <Text style={styles.profileName}>{myProfile.name}</Text>
                <Text style={{color: '#6b7280', marginTop: 8, textAlign: 'center', paddingHorizontal: 20}}>
                  {myProfile.bio || "Escreve algo sobre ti..."}
                </Text>

                <View style={styles.profileButtonRow}>
                  <TouchableOpacity style={styles.editBtn}>
                    <Text style={styles.editBtnText}>‚úèÔ∏è Editar Perfil</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ) : (
               <Text style={{textAlign: 'center', marginTop: 20}}>A carregar perfil...</Text>
            )}

            <TouchableOpacity style={styles.logoutBtn} onPress={handleLogout}>
              <Text style={styles.logoutBtnText}>Terminar Sess√£o</Text>
            </TouchableOpacity>
          </ScrollView>
        )}
      </View>

      {/* BARRA DE NAVEGA√á√ÉO INFERIOR */}
      <View style={styles.bottomNav}>
        <TouchableOpacity onPress={() => setTab("match")} style={styles.navBtn}>
          <Text style={[styles.navIcon, { color: tab === "match" ? "#4f46e5" : "#9ca3af" }]}>üè†</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => setTab("chat")} style={styles.navBtn}>
          <Text style={[styles.navIcon, { color: tab === "chat" ? "#4f46e5" : "#9ca3af" }]}>üí¨</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => setTab("profile")} style={styles.navBtn}>
          <Text style={[styles.navIcon, { color: tab === "profile" ? "#4f46e5" : "#9ca3af" }]}>üë§</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

// ESTILOS
const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: "#f9fafb" },
  content: { flex: 1 },
  page: { flex: 1, padding: 16 },

  // Cart√£o de Match
  card: {
    height: 500, // Altura fixa para ficar bonito
    borderRadius: 16,
    overflow: "hidden",
    elevation: 4,
    backgroundColor: "#000",
    marginBottom: 20,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  cardImage: {
    width: "100%",
    height: "100%",
    resizeMode: "cover",
  },
  cardInfoContainer: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    padding: 24,
    paddingTop: 60, // Gradiente suave
  },
  cardName: {
    color: "#fff",
    fontSize: 30,
    fontWeight: "700",
  },
  tagRow: {
    flexDirection: "row",
    flexWrap: "wrap",
    gap: 8,
    marginTop: 12,
  },
  tag: {
    backgroundColor: "rgba(255,255,255,0.2)",
    paddingHorizontal: 10,
    paddingVertical: 4,
    borderRadius: 999,
  },
  tagText: {
    color: "#fff",
    fontSize: 12,
    fontWeight: "600",
  },

  // Perfil
  profileTitle: {
    fontSize: 30,
    fontWeight: "700",
    color: "#374151",
    marginBottom: 16,
    textAlign: "center"
  },
  profileHeader: {
    alignItems: "center",
