    textAlign: "center",
    marginTop: 16,
    color: "#374151",
  },
  profileButtonRow: {
    flexDirection: "row",
    marginTop: 24,
    gap: 16,
    width: "100%",
    paddingHorizontal: 20
  },
  editBtn: {
    flex: 1,
    backgroundColor: "#e5e7eb",
    paddingVertical: 12,
    borderRadius: 12,
    alignItems: "center",
    justifyContent: "center",
  },
  editBtnText: {
    fontWeight: "700",
    color: "#374151",
    fontSize: 16,
  },
  logoutBtn: {
    marginTop: 10,
    marginBottom: 40,
    backgroundColor: "rgba(239, 68, 68, 0.1)",
    paddingVertical: 12,
    borderRadius: 12,
    alignItems: "center",
    marginHorizontal: 20
  },
  logoutBtnText: {
    color: "#ef4444",
    fontWeight: "700",
    fontSize: 16,
  },

  // Barra de Navega√ß√£o
  bottomNav: {
    height: 70,
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    borderTopWidth: 1,
    borderColor: "#e5e7eb",
    backgroundColor: "#fff",
  },
  navBtn: {
    padding: 10,
  },
  navIcon: {
    fontSize: 28,
  },
});
