import React, { useState } from "react";
import { 
  View, 
  Text, 
  TextInput, 
  TouchableOpacity, 
  StyleSheet, 
  ScrollView, 
  SafeAreaView,
  Alert,
  ActivityIndicator,
  Image, // Importante para mostrar a pr√©via
  Platform
} from "react-native";
import axios from "axios";
import * as ImagePicker from 'expo-image-picker'; // Import da galeria
import { ScreenKey } from "../../App";

// SEU ENDERE√áO (Ajuste a porta se necess√°rio, ex: 8080 ou 8025)
const API_URL = "http://localhost:8080"; 

export default function RegisterFlow({ onNavigate }: { onNavigate: (s: ScreenKey) => void }) {
  const [step, setStep] = useState(1);
  const total = 5;
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  // DADOS DO USU√ÅRIO
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [birthDate, setBirthDate] = useState("");
  const [bio, setBio] = useState("");

  // DADOS DA FOTO
  const [selectedImage, setSelectedImage] = useState<ImagePicker.ImagePickerAsset | null>(null);

  const clearError = () => setErrorMessage("");

  // Valida√ß√£o de data (AAAA-MM-DD)
  const isValidDate = (dateString: string) => {
    const regex = /^\d{4}-\d{2}-\d{2}$/;
    if (!regex.test(dateString)) return false;
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return false;
    return true;
  };

  // --- ABRIR GALERIA ---
  const pickImage = async () => {
    // Pede permiss√£o para acessar a galeria
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (permissionResult.granted === false) {
      alert("√â necess√°rio permitir o acesso √† galeria!");
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true, // Permite recortar a foto
      aspect: [4, 4], // Quadrado
      quality: 0.7, // Qualidade levemente reduzida para n√£o pesar o banco
    });

    if (!result.canceled) {
      setSelectedImage(result.assets[0]);
      clearError();
    }
  };

  // --- NAVEGA√á√ÉO ENTRE PASSOS ---
  function nextStep() {
    setErrorMessage(""); 

    if (step === 1) {
      if (!email.includes("@")) return setErrorMessage("E-mail inv√°lido.");
      if (password.length < 6) return setErrorMessage("Senha muito curta (m√≠n 6).");
    }

    if (step === 2) {
      if (!name.trim()) return setErrorMessage("Nome √© obrigat√≥rio.");
      if (!isValidDate(birthDate)) return setErrorMessage("Data inv√°lida. Use AAAA-MM-DD.");
    }

    // Passo 3 √© a foto (opcional, mas recomendado)

    if (step === 4) {
      if (!bio.trim()) return setErrorMessage("Escreva uma biografia.");
    }

    if (step < total) {
      setStep(step + 1);
    } else {
      handleFinishRegistration();
    }
  }

  function prevStep() {
    setErrorMessage("");
    if (step > 1) setStep(step - 1);
    else onNavigate("welcome");
  }

  // --- FINALIZAR CADASTRO ---
  const handleFinishRegistration = async () => {
    if(loading) return;
    setLoading(true);
    setErrorMessage("");

    try {
      // 1. CRIAR USU√ÅRIO
      console.log("Criando usu√°rio...");
      const resUser = await axios.post(`${API_URL}/api/auth/register`, { email, password });
      const userId = resUser.data.id;

      // 2. LOGIN (Pegar Token)
      console.log("Autenticando...");
      const resLogin = await axios.post(`${API_URL}/api/auth/login`, { email, password });
      const token = resLogin.data.token;

      // Cabe√ßalho para JSON
      const jsonConfig = { headers: { Authorization: `Bearer ${token}` } };

      // 3. CRIAR PERFIL
      console.log("Criando perfil...");
      const resProfile = await axios.put(`${API_URL}/api/profiles/user/${userId}`, {
        name, birthDate, bio, lastLocation: "Brasil"
      }, jsonConfig);

      const profileId = resProfile.data.id;

      // 4. UPLOAD DA FOTO (Se selecionada)
      if (selectedImage) {
        console.log("Enviando foto para o ID:", profileId);

        // Criar o FormData para envio de arquivo
        const formData = new FormData();

        // Adiciona o arquivo. Note o "as any" para contornar tipagem do TS no React Native
        formData.append('file', {
          uri: selectedImage.uri,
          name: 'profile_photo.jpg',
          type: 'image/jpeg',
        } as any);

        // Adiciona o ID do perfil como texto
        formData.append('profileId', String(profileId));

        // Envia para o endpoint novo do PhotoController
        await axios.post(`${API_URL}/photos/upload`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data', // Essencial para envio de arquivo
            // Authorization n√£o √© estritamente necess√°rio pois o endpoint est√° como permitAll no SecurityConfig,
            // mas pode mandar se quiser garantir.
          },
        });
      }

      if (Platform.OS === 'web') alert("Cadastro realizado!");
      else Alert.alert("Sucesso", "Cadastro realizado!");

      onNavigate("login");

    } catch (error: any) {
      console.error("Erro:", error.response?.data || error.message);
      setErrorMessage("Erro ao finalizar cadastro. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.back} onPress={prevStep}><Text style={styles.backText}>‚Äπ</Text></TouchableOpacity>
        <View style={styles.progress}><View style={[styles.progressBar, { width: `${(step / total) * 100}%` }]} /></View>
      </View>

      <ScrollView contentContainerStyle={styles.content}>

        {step === 1 && (
          <View>
            <Text style={styles.title}>Dados de Acesso</Text>
            <TextInput placeholder="E-mail" style={styles.input} value={email} onChangeText={(t)=>{setEmail(t); clearError()}} autoCapitalize="none" keyboardType="email-address"/>
            <TextInput placeholder="Senha" style={styles.input} value={password} onChangeText={(t)=>{setPassword(t); clearError()}} secureTextEntry/>
          </View>
        )}

        {step === 2 && (
          <View>
            <Text style={styles.title}>Sobre voc√™</Text>
            <TextInput placeholder="Nome" style={styles.input} value={name} onChangeText={(t)=>{setName(t); clearError()}}/>
            <Text style={styles.label}>Data de Nascimento (AAAA-MM-DD)</Text>
            <TextInput placeholder="Ex: 2000-05-20" style={styles.input} value={birthDate} onChangeText={(t)=>{setBirthDate(t); clearError()}} keyboardType="numeric"/>
          </View>
        )}

        {/* --- PASSO 3: FOTO DA GALERIA --- */}
        {step === 3 && (
          <View>
            <Text style={styles.title}>Sua melhor foto</Text>
            <Text style={styles.subtitle}>Escolha uma foto da sua galeria.</Text>

            <TouchableOpacity style={styles.galleryBtn} onPress={pickImage}>
              <Text style={styles.galleryBtnText}>
                {selectedImage ? "Trocar Foto" : "üì∑ Abrir Galeria"}
              </Text>
            </TouchableOpacity>

            {/* Pr√©via da Imagem */}
            {selectedImage ? (
              <View style={styles.previewContainer}>
                <Image source={{ uri: selectedImage.uri }} style={styles.previewImage} />
              </View>
            ) : (
              <View style={[styles.previewContainer, {backgroundColor: '#f3f4f6'}]}>
                <Text style={{color: '#9ca3af'}}>Nenhuma foto selecionada</Text>
              </View>
            )}
          </View>
        )}

        {step === 4 && (
          <View>
            <Text style={styles.title}>Biografia</Text>
            <TextInput placeholder="Conte algo sobre voc√™..." style={[styles.input, { height: 120, textAlignVertical: "top" }]} multiline value={bio} onChangeText={(t)=>{setBio(t); clearError()}}/>
          </View>
        )}

        {step === 5 && (
          <View>
            <Text style={styles.title}>Tudo pronto!</Text>
            <Text style={styles.subtitle}>Vamos encontrar o seu par ideal.</Text>
            <View style={styles.tagsRow}>
              <TouchableOpacity style={[styles.tag, {backgroundColor: '#4f46e5'}]}><Text style={[styles.tagText, {color: '#fff'}]}>Amizade</Text></TouchableOpacity>
              <TouchableOpacity style={styles.tag}><Text style={styles.tagText}>Namoro</Text></TouchableOpacity>
            </View>
          </View>
        )}

        {errorMessage ? <View style={styles.errorContainer}><Text style={styles.errorText}>‚ö†Ô∏è {errorMessage}</Text></View> : null}

      </ScrollView>

      <View style={styles.footer}>
        <TouchableOpacity style={[styles.nextBtn, loading && {backgroundColor: '#a5b4fc'}]} onPress={nextStep} disabled={loading}>
          {loading ? <ActivityIndicator color="#fff" /> : <Text style={styles.nextText}>{step < total ? "Continuar" : "Finalizar"}</Text>}
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: "#ffffff" },
  header: { paddingTop: 24, paddingHorizontal: 24 },
  back: { position: "absolute", top: 24, left: 24, zIndex: 10, padding: 8 },
  backText: { fontSize: 28, color: "#6b7280" },
  progress: { backgroundColor: "#e5e7eb", borderRadius: 999, height: 6, overflow: "hidden" },
  progressBar: { height: 6, backgroundColor: "#4f46e5", borderRadius: 999 },
