import React, { useState } from "react";
import {
  View,
  Text,
  Image,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  SafeAreaView,
  ActivityIndicator,
  Platform,
  Alert
} from "react-native";
import * as ImagePicker from 'expo-image-picker';
import DateTimePicker from '@react-native-community/datetimepicker';
import axios from "axios";
import { API_URL } from "./config";
import { ScreenKey } from "../../App";

interface RegisterFlowProps {
  onNavigate: (screen: ScreenKey) => void;
}

export default function RegisterFlow({ onNavigate }: RegisterFlowProps) {
  const [step, setStep] = useState(1);
  const total = 4;
  const [loading, setLoading] = useState(false);

  // ESTADO PARA MENSAGEM DE ERRO
  const [errorMessage, setErrorMessage] = useState("");

  // DADOS DO FORMUL√ÅRIO
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [birthDate, setBirthDate] = useState(""); // String formatada YYYY-MM-DD
  const [bio, setBio] = useState("");
  const [imageUri, setImageUri] = useState<string | null>(null);

  // ESTADO DO CALEND√ÅRIO
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [dateObject, setDateObject] = useState(new Date()); // Objeto Data para o componente

  const clearError = () => setErrorMessage("");

  // FUN√á√ÉO QUE FORMATA A DATA AO ESCOLHER NO CALEND√ÅRIO
  const onChangeDate = (event: any, selectedDate?: Date) => {
    setShowDatePicker(false); // Fecha o calend√°rio
    if (selectedDate) {
      setDateObject(selectedDate);

      // Formata para YYYY-MM-DD manualmente para evitar problemas de fuso hor√°rio
      const year = selectedDate.getFullYear();
      const month = String(selectedDate.getMonth() + 1).padStart(2, '0');
      const day = String(selectedDate.getDate()).padStart(2, '0');

      const formatted = `${year}-${month}-${day}`;
      setBirthDate(formatted);
      clearError();
    }
  };

  // --- L√ìGICA DE NAVEGA√á√ÉO (STEPS) ---
  function nextStep() {
    setErrorMessage("");

    if (step === 1) {
      if (!email.includes("@")) {
        setErrorMessage("Por favor, insira um e-mail v√°lido.");
        return;
      }
      if (password.length < 6) {
        setErrorMessage("A senha deve ter no m√≠nimo 6 caracteres.");
        return;
      }
    }

    if (step === 2) {
      if (!name.trim()) {
        setErrorMessage("O campo nome √© obrigat√≥rio.");
        return;
      }
      if (!birthDate) {
        setErrorMessage("Por favor, selecione sua data de nascimento.");
        return;
      }
    }

    if (step === 4) {
      if (!bio.trim()) {
        setErrorMessage("Escreva algo na sua biografia.");
        return;
      }
    }

    if (step < total) {
      setStep(step + 1);
    } else {
      handleFinishRegistration();
    }
  }

  function prevStep() {
    setErrorMessage("");
    if (step > 1) setStep(step - 1);
    else onNavigate("welcome");
  }

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.8,
    });

    if (!result.canceled) {
      setImageUri(result.assets[0].uri);
      clearError();
    }
  };

  // --- CADASTRO COMPLETO ---
  const handleFinishRegistration = async () => {
    if (loading) return;
    setLoading(true);
    setErrorMessage("");

    try {
      // 1. CRIAR USU√ÅRIO
      console.log("1. Criando usu√°rio...");
      const resUser = await axios.post(`${API_URL}/api/auth/register`, { email, password });
      const userId = resUser.data.id;

      // 2. LOGIN (Para pegar o Token JWT)
      console.log("2. Autenticando...");
      const resLogin = await axios.post(`${API_URL}/api/auth/login`, { email, password });
      const token = resLogin.data.token;

      // 3. CRIAR PERFIL
      console.log("3. Criando perfil...");
      await axios.put(`${API_URL}/api/profiles/user/${userId}`, {
        name,
        birthDate, // J√° est√° no formato YYYY-MM-DD
        bio,
        lastLocation: "Brasil"
      }, { headers: { Authorization: `Bearer ${token}` } });

      const resProfile = await axios.get(`${API_URL}/api/profiles/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const profileId = resProfile.data.id;

      // 4. UPLOAD DA FOTO (USANDO FETCH PARA ANDROID)
      if (imageUri) {
        console.log("4. Enviando foto...");
        const formData = new FormData();
        formData.append('profileId', String(profileId));

        if (Platform.OS === 'web') {
          const response = await fetch(imageUri);
          const blob = await response.blob();
          formData.append('file', blob, 'profile.jpg');
        } else {
          const filename = imageUri.split('/').pop();
          const match = /\.(\w+)$/.exec(filename || '');
          const type = match ? `image/${match[1]}` : `image/jpeg`;

          // @ts-ignore
          formData.append('file', {
            uri: imageUri,
            name: filename || 'profile.jpg',
            type: type,
          });
        }

        const uploadResponse = await fetch(`${API_URL}/photos/upload`, {
          method: 'POST',
          body: formData,
          headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': 'application/json',
          },
        });

        if (!uploadResponse.ok) {
          throw new Error("Falha no upload da imagem");
        }
      }

      if (Platform.OS === 'web') alert("Conta criada com sucesso!");
      else Alert.alert("Sucesso", "Conta criada com sucesso!");

      onNavigate("login");

    } catch (error: any) {
      console.error("Erro completo:", error);
      let msg = "Erro desconhecido.";
      if (error.response?.data?.message) msg = error.response.data.message;
      else if (error.message) msg = error.message;
      setErrorMessage(`Falha no cadastro: ${msg}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.back} onPress={prevStep}>
          <Text style={styles.backText}>‚Äπ</Text>
        </TouchableOpacity>
        <View style={styles.progress}>
          <View style={[styles.progressBar, { width: `${(step / total) * 100}%` }]} />
        </View>
      </View>

      <ScrollView contentContainerStyle={styles.content}>

        {step === 1 && (
          <View>
            <Text style={styles.title}>Seus dados de acesso</Text>
            <TextInput
              placeholder="E-mail"
              style={styles.input}
              value={email}
              onChangeText={(t) => { setEmail(t); clearError(); }}
              autoCapitalize="none"
              keyboardType="email-address"
            />
            <TextInput
              placeholder="Senha (m√≠n 6 caracteres)"
              style={styles.input}
              secureTextEntry
              value={password}
              onChangeText={(t) => { setPassword(t); clearError(); }}
            />
          </View>
        )}

        {step === 2 && (
          <View>
            <Text style={styles.title}>Como podemos cham√°-lo?</Text>
            <TextInput
              placeholder="Nome"
              style={styles.input}
              value={name}
              onChangeText={(t) => { setName(t); clearError(); }}
            />

            <Text style={styles.label}>Data de nascimento</Text>

            {/* --- INPUT DE DATA CLIC√ÅVEL --- */}
            <TouchableOpacity onPress={() => setShowDatePicker(true)} style={[styles.input, { justifyContent: 'center' }]}>
              <Text style={{ color: birthDate ? "#111827" : "#9ca3af", fontSize: 16 }}>
                {birthDate || "Selecionar Data üìÖ"}
              </Text>
            </TouchableOpacity>

            {/* --- COMPONENTE DO CALEND√ÅRIO --- */}
            {showDatePicker && (
              <DateTimePicker
                value={dateObject}
                mode="date"
                display="default" // No Android abre o pop-up, no iOS o spinner
                onChange={onChangeDate}
                maximumDate={new Date()} // Impede datas futuras
              />
            )}
          </View>
        )}

        {step === 3 && (
          <View>
            <Text style={styles.title}>Sua foto de perfil</Text>
            <Text style={styles.subtitle}>Escolha uma foto da sua galeria.</Text>

            <View style={{ alignItems: 'center', marginBottom: 20 }}>
              {imageUri ? (
                <Image source={{ uri: imageUri }} style={{ width: 120, height: 120, borderRadius: 60, marginBottom: 10 }} />
              ) : (
                <View style={{ width: 120, height: 120, borderRadius: 60, backgroundColor: '#e5e7eb', marginBottom: 10 }} />
              )}
              <TouchableOpacity onPress={pickImage} style={styles.uploadBtn}>
                <Text style={styles.uploadBtnText}>{imageUri ? "Trocar Foto" : "Selecionar Foto"}</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}

        {step === 4 && (
          <View>
            <Text style={styles.title}>Fale um pouco sobre si</Text>
            <TextInput
              placeholder="O que voc√™ gosta de fazer?"
              style={[styles.input, { height: 160, textAlignVertical: "top" }]}
              multiline
              value={bio}
              onChangeText={(t) => { setBio(t); clearError(); }}
            />
          </View>
        )}

        {errorMessage ? (
          <View style={styles.errorContainer}>
            <Text style={styles.errorText}>‚ö†Ô∏è {errorMessage}</Text>
          </View>
        ) : null}

      </ScrollView>

      <View style={styles.footer}>
        <TouchableOpacity
          style={[styles.nextBtn, loading && { backgroundColor: '#a5b4fc' }]}
          onPress={nextStep}
          disabled={loading}
        >
          {loading ? <ActivityIndicator color="#fff" /> :
            <Text style={styles.nextText}>{step < total ? "Continuar" : "Finalizar Cadastro"}</Text>
          }
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

