import React, { useState, useEffect, useRef } from "react";
import { 
  View, 
  Text, 
  StyleSheet, 
  Image, 
  TouchableOpacity, 
  TextInput, 
  ScrollView, 
  SafeAreaView,
  ActivityIndicator,
  Alert,
  KeyboardAvoidingView,
  Platform
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { LinearGradient } from "expo-linear-gradient";
import { ScreenKey } from "../../App";

const API_URL = "http://localhost:8080"; 

type Tab = "match" | "chat" | "profile";

export default function MainApp({ onNavigate }: { onNavigate: (s: ScreenKey) => void }) {
  const [tab, setTab] = useState<Tab>("match");
  const [loading, setLoading] = useState(false);

  // DADOS GERAIS
  const [userId, setUserId] = useState<number | null>(null);
  const [matchData, setMatchData] = useState<any>(null);
  const [myProfile, setMyProfile] = useState<any>(null);

  // DADOS DO CHAT
  const [messages, setMessages] = useState<any[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const scrollViewRef = useRef<ScrollView>(null);

  // Carrega dados ao iniciar
  useEffect(() => {
    loadData();
  }, []);

  // Carrega mensagens quando muda para a aba CHAT
  useEffect(() => {
    if (tab === "chat" && matchData?.id) {
      loadMessages();
      // Opcional: Criar um intervalo para atualizar mensagens a cada 5 segundos
      const interval = setInterval(loadMessages, 5000);
      return () => clearInterval(interval);
    }
  }, [tab, matchData]);

  const loadData = async () => {
    setLoading(true);
    try {
      const token = await AsyncStorage.getItem("userToken");
      const storedId = await AsyncStorage.getItem("userId");

      if (!token || !storedId) {
        onNavigate("login");
        return;
      }
      setUserId(Number(storedId));

      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // 1. MATCH DO DIA
      try {
        const resMatch = await axios.get(`${API_URL}/api/matches/today`);
        setMatchData(resMatch.data);
      } catch (error) {
        console.log("Sem match hoje.");
        setMatchData(null); 
      }

      // 2. MEU PERFIL
      try {
        const resProfile = await axios.get(`${API_URL}/api/profiles/user/${storedId}`);
        setMyProfile(resProfile.data);
      } catch (error) {
        console.log("Erro perfil:", error);
      }

    } catch (err) {
      Alert.alert("Erro", "Falha ao carregar dados.");
    } finally {
      setLoading(false);
    }
  };

  const loadMessages = async () => {
    if (!matchData?.id) return;
    try {
      // GET /api/messages/match/{id}
      const res = await axios.get(`${API_URL}/api/messages/match/${matchData.id}`);
      setMessages(res.data);
    } catch (error) {
      console.log("Erro ao carregar mensagens");
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !matchData || !userId) return;

    // O Backend precisa saber quem envia e quem recebe
    // O ID do matchData.matchedProfile.userId √© o ID do "outro"
    const receiverId = matchData.matchedProfile.userId;

    try {
      // POST /api/messages/match/{id}
      await axios.post(`${API_URL}/api/messages/match/${matchData.id}`, {
        text: newMessage,
        senderId: userId,
        receiverId: receiverId
      });

      setNewMessage(""); // Limpa o campo
      loadMessages(); // Recarrega a lista

      // Rola para baixo
      setTimeout(() => scrollViewRef.current?.scrollToEnd({ animated: true }), 100);

    } catch (error) {
      Alert.alert("Erro", "N√£o foi poss√≠vel enviar a mensagem.");
    }
  };

  const handleLogout = async () => {
    await AsyncStorage.clear();
    onNavigate("welcome");
  };

  if (loading) {
    return (
      <View style={styles.center}>
        <ActivityIndicator size="large" color="#4f46e5" />
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>

        {/* === ABA 1: MATCH === */}
        {tab === "match" && (
          <ScrollView style={styles.page} contentContainerStyle={{flexGrow: 1}}>
            <View style={{ alignItems: "center", marginVertical: 16 }}>
              <Text style={{ color: "#6b7280", fontSize: 14 }}>Seu match expira em</Text>
              <Text style={{ fontSize: 24, fontWeight: "700", color: "#374151" }}>23:59:59</Text>
            </View>

            {matchData ? (
              <TouchableOpacity style={styles.card} activeOpacity={0.9} onPress={() => setTab("chat")}>
                <Image 
                  source={{ uri: matchData.matchedProfile.photos?.[0]?.url || "https://via.placeholder.com/400" }} 
                  style={styles.cardImage} 
                />
                <LinearGradient colors={["transparent", "rgba(0,0,0,0.9)"]} style={styles.cardInfoContainer}>
                  <Text style={styles.cardName}>{matchData.matchedProfile.name}</Text>
                  <Text style={{color: '#e5e7eb', fontSize: 16, marginTop: 4}}>
                    {matchData.matchedProfile.bio || "Sem bio."}
                  </Text>
                  <View style={styles.tagRow}>
                    <View style={styles.tag}>
                      <Text style={styles.tagText}>üí¨ Tocar para conversar</Text>
                    </View>
                  </View>
                </LinearGradient>
              </TouchableOpacity>
            ) : (
              <View style={[styles.card, styles.noMatchCard]}>
                <Text style={{fontSize: 50}}>üò¥</Text>
                <Text style={{color: 'white', textAlign: 'center', marginTop: 10}}>Sem novos matches hoje.</Text>
                <TouchableOpacity onPress={loadData} style={styles.retryBtn}>
                  <Text style={{fontWeight: 'bold'}}>Atualizar</Text>
                </TouchableOpacity>
              </View>
            )}
          </ScrollView>
        )}

        {/* === ABA 2: CHAT === */}
        {tab === "chat" && (
          <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : undefined} style={{flex: 1}}>
            {matchData ? (
              <>
                <View style={styles.chatHeader}>
                  <Image source={{ uri: matchData.matchedProfile.photos?.[0]?.url || "https://via.placeholder.com/50" }} style={styles.chatAvatar} />
                  <Text style={styles.chatName}>{matchData.matchedProfile.name}</Text>
                </View>

                <ScrollView 
                  style={styles.chatBody} 
                  ref={scrollViewRef}
                  onContentSizeChange={() => scrollViewRef.current?.scrollToEnd({ animated: true })}
                >
                  {messages.length === 0 && (
                    <Text style={{textAlign: 'center', color: '#9ca3af', marginTop: 20}}>
                      Nenhuma mensagem. Diga "Oi"! üëã
                    </Text>
                  )}

                  {messages.map((msg) => {
                    const isMe = msg.senderId === userId;
                    return (
                      <View key={msg.id} style={[styles.messageRow, { justifyContent: isMe ? "flex-end" : "flex-start" }]}>
                        <View style={[styles.messageBubble, isMe ? styles.messageSent : styles.messageReceived]}>
                          <Text style={{color: isMe ? '#fff' : '#374151'}}>{msg.text}</Text>
                        </View>
                      </View>
                    );
                  })}
                  <View style={{height: 20}} />
                </ScrollView>

                <View style={styles.chatInputContainer}>
                  <TextInput
                    placeholder="Escreva uma mensagem..."
                    style={styles.chatInput}
                    value={newMessage}
                    onChangeText={setNewMessage}
                    placeholderTextColor="#6b7280"
                  />
                  <TouchableOpacity style={styles.sendButton} onPress={handleSendMessage}>
                    <Text style={styles.sendButtonText}>‚û§</Text>
                  </TouchableOpacity>
                </View>
              </>
            ) : (
              <View style={styles.center}>
                <Text style={{color: '#6b7280'}}>Voc√™ precisa de um match para conversar!</Text>
              </View>
            )}
          </KeyboardAvoidingView>
        )}

        {/* === ABA 3: PERFIL === */}
        {tab === "profile" && (
          <ScrollView style={styles.page}>
            <Text style={styles.profileTitle}>Meu Perfil</Text>
            {myProfile ? (
              <View style={styles.profileHeader}>
                <Image source={{ uri: myProfile.photos?.[0]?.url || "https://github.com/github.png" }} style={styles.profileAvatar} />
                <Text style={styles.profileName}>{myProfile.name}</Text>
                <Text style={{color: '#6b7280', marginTop: 8}}>{myProfile.bio}</Text>
              </View>
            ) : (
               <Text style={{textAlign: 'center'}}>Carregando...</Text>
            )}
            <TouchableOpacity style={styles.logoutBtn} onPress={handleLogout}>
              <Text style={styles.logoutBtnText}>Sair</Text>
            </TouchableOpacity>
          </ScrollView>
        )}
      </View>

      {/* NAVEGA√á√ÉO */}
      <View style={styles.bottomNav}>
        <TouchableOpacity onPress={() => setTab("match")} style={styles.navBtn}><Text style={[styles.navIcon, {color: tab==="match"?"#4f46e5":"#9ca3af"}]}>üè†</Text></TouchableOpacity>
        <TouchableOpacity onPress={() => setTab("chat")} style={styles.navBtn}><Text style={[styles.navIcon, {color: tab==="chat"?"#4f46e5":"#9ca3af"}]}>üí¨</Text></TouchableOpacity>
        <TouchableOpacity onPress={() => setTab("profile")} style={styles.navBtn}><Text style={[styles.navIcon, {color: tab==="profile"?"#4f46e5":"#9ca3af"}]}>üë§</Text></TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

