import React, { useState, useEffect, useRef } from "react";
import { 
  View, 
  Text, 
  StyleSheet, 
  Image, 
  TouchableOpacity, 
  TextInput, 
  ScrollView, 
  SafeAreaView,
  ActivityIndicator,
  Alert,
  KeyboardAvoidingView,
  Platform,
  Modal 
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { LinearGradient } from "expo-linear-gradient";
import * as ImagePicker from 'expo-image-picker'; // Import da Galeria
import { ScreenKey } from "../../App";

// SEU ENDEREÃ‡O (Ajuste a porta se necessÃ¡rio: 8080 ou 8025)
const API_URL = "http://localhost:8080"; 

type Tab = "match" | "chat" | "profile";

export default function MainApp({ onNavigate }: { onNavigate: (s: ScreenKey) => void }) {
  const [tab, setTab] = useState<Tab>("match");
  const [loading, setLoading] = useState(false);

  // DADOS
  const [userId, setUserId] = useState<number | null>(null);
  const [userToken, setUserToken] = useState<string | null>(null);
  const [matchData, setMatchData] = useState<any>(null);
  const [myProfile, setMyProfile] = useState<any>(null);

  // CHAT
  const [messages, setMessages] = useState<any[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const scrollViewRef = useRef<ScrollView>(null);
  const [timeLeft, setTimeLeft] = useState("--:--:--");

  // EDIÃ‡ÃƒO
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [editBio, setEditBio] = useState("");
  const [editLocation, setEditLocation] = useState("");

  // FOTO
  const [selectedImage, setSelectedImage] = useState<ImagePicker.ImagePickerAsset | null>(null);
  const [uploadingPhoto, setUploadingPhoto] = useState(false);

  // 1. INICIALIZAÃ‡ÃƒO
  useEffect(() => {
    async function init() {
      setLoading(true);
      try {
        const token = await AsyncStorage.getItem("userToken");
        const storedId = await AsyncStorage.getItem("userId");
        if (!token || !storedId) { onNavigate("login"); return; }
        setUserToken(token);
        setUserId(Number(storedId));
        await loadInitialData(token, storedId);
      } catch (e) { onNavigate("login"); } 
      finally { setLoading(false); }
    }
    init();
  }, []);

  // 2. ATUALIZAÃ‡Ã•ES EM TEMPO REAL
  useEffect(() => {
    const interval = setInterval(() => {
      if (tab === "chat" && matchData?.id && userToken) loadMessages();
      if (matchData?.expiresAt) calculateTimeLeft();
    }, 1000);
    return () => clearInterval(interval);
  }, [tab, matchData, userToken]);

  const calculateTimeLeft = () => {
    if (!matchData?.expiresAt) return;
    const now = new Date().getTime();
    const distance = new Date(matchData.expiresAt).getTime() - now;
    if (distance < 0) setTimeLeft("EXPIRADO");
    else {
      const h = Math.floor((distance % (86400000)) / 3600000);
      const m = Math.floor((distance % 3600000) / 60000);
      const s = Math.floor((distance % 60000) / 1000);
      setTimeLeft(`${h<10?'0'+h:h}:${m<10?'0'+m:m}:${s<10?'0'+s:s}`);
    }
  };

  const loadInitialData = async (token: string, id: string) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const resMatch = await axios.get(`${API_URL}/api/matches/today`, config);
      setMatchData(resMatch.data);
    } catch (error) { setMatchData(null); }
    try {
      const resProfile = await axios.get(`${API_URL}/api/profiles/user/${id}`, config);
      setMyProfile(resProfile.data);
    } catch (error) { console.log("Erro perfil"); }
  };

  const loadMessages = async () => {
    if (!matchData?.id || !userToken) return;
    try {
      const res = await axios.get(`${API_URL}/api/messages/match/${matchData.id}`, 
        { headers: { Authorization: `Bearer ${userToken}` } });
      if (res.data.length !== messages.length) {
        setMessages(res.data);
        setTimeout(() => scrollViewRef.current?.scrollToEnd({ animated: true }), 100);
      }
    } catch (error) { }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !matchData || !userId || !userToken) return;
    try {
      await axios.post(`${API_URL}/api/messages/match/${matchData.id}`, 
        { text: newMessage, senderId: userId, receiverId: matchData.matchedProfile.userId }, 
        { headers: { Authorization: `Bearer ${userToken}` } });
      setNewMessage(""); loadMessages();
    } catch (error) { Alert.alert("Erro", "Falha ao enviar."); }
  };

  // --- LÃ“GICA DE EDIÃ‡ÃƒO ---
  const openEditModal = () => {
    if (myProfile) {
      setEditName(myProfile.name);
      setEditBio(myProfile.bio || "");
      setEditLocation(myProfile.lastLocation || "");
      setSelectedImage(null); // Reseta seleÃ§Ã£o de foto
      setIsEditing(true);
    }
  };

  const saveProfile = async () => {
    if (!userToken || !userId || !myProfile) return;
    try {
      const config = { headers: { Authorization: `Bearer ${userToken}` } };
      await axios.put(`${API_URL}/api/profiles/user/${userId}`, {
        name: editName, bio: editBio, lastLocation: editLocation, birthDate: myProfile.birthDate
      }, config);

      Alert.alert("Sucesso", "Perfil atualizado!");
      loadInitialData(userToken, String(userId)); 
      setIsEditing(false);
    } catch (error) { Alert.alert("Erro", "NÃ£o foi possÃ­vel salvar."); }
  };

  // --- GALERIA E UPLOAD ---
  const pickImage = async () => {
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert("Ã‰ necessÃ¡rio permitir acesso Ã  galeria!");
      return;
    }
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1], // Quadrado para ficar bonito no perfil
      quality: 0.7,
    });

    if (!result.canceled) {
      setSelectedImage(result.assets[0]);
    }
  };

  // --- UPLOAD USANDO FETCH (CORREÃ‡ÃƒO DEFINITIVA) ---
  const handleUploadPhoto = async () => {
    if (!selectedImage || !myProfile || !userToken) {
      Alert.alert("Erro", "Selecione uma imagem primeiro.");
      return;
    }
    setUploadingPhoto(true);

    try {
      const formData = new FormData();
      // @ts-ignore
      formData.append('file', {
        uri: selectedImage.uri,
        name: 'photo.jpg',
        type: 'image/jpeg',
      });
      formData.append('profileId', String(myProfile.id));

      // USANDO FETCH EM VEZ DE AXIOS PARA UPLOAD
      // O fetch lida melhor com multipart no React Native
      const response = await fetch(`${API_URL}/api/photos/upload`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${userToken}`,
          // NÃ£o colocamos Content-Type, o fetch faz isso sozinho!
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Erro no envio: ' + response.status);
      }

      Alert.alert("Sucesso", "Foto adicionada!");
      setSelectedImage(null);

      // Recarrega o perfil
      const resProfile = await axios.get(`${API_URL}/api/profiles/user/${userId}`, { 
        headers: { Authorization: `Bearer ${userToken}` } 
      });
      setMyProfile(resProfile.data);

    } catch (error) {
      console.log(error);
      Alert.alert("Erro", "Falha ao enviar a foto.");
    } finally {
      setUploadingPhoto(false);
    }
  };

  // --- DELETE COM O LINK CORRIGIDO ---
  const handleDeletePhoto = async (photoId: number) => {
    if (!userToken) return;
    try {
      // Agora aponta para /api/photos/...
      await axios.delete(`${API_URL}/api/photos/${photoId}`, { 
        headers: { Authorization: `Bearer ${userToken}` } 
      });

      Alert.alert("Removida", "Foto apagada.");
      const res = await axios.get(`${API_URL}/api/profiles/user/${userId}`, { 
        headers: { Authorization: `Bearer ${userToken}` } 
      });
      setMyProfile(res.data);
    } catch (error) { 
      Alert.alert("Erro", "NÃ£o foi possÃ­vel apagar."); 
    }
  };

  const handleLogout = async () => { await AsyncStorage.clear(); onNavigate("welcome"); };

  if (loading) return <View style={styles.center}><ActivityIndicator color="#4f46e5" /></View>;

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>

        {/* ABA MATCH */}
        {tab === "match" && (
          <ScrollView style={styles.page} contentContainerStyle={{flexGrow: 1}}>
            <View style={{ alignItems: "center", marginVertical: 16 }}>
              <Text style={{ color: "#6b7280", fontSize: 14 }}>Expira em</Text>
              <Text style={{ fontSize: 32, fontWeight: "700", color: "#4f46e5", fontVariant: ['tabular-nums'] }}>{matchData ? timeLeft : "--:--:--"}</Text>
            </View>
            {matchData ? (
              <TouchableOpacity style={styles.card} activeOpacity={0.9} onPress={() => setTab("chat")}>
                <Image source={{ uri: matchData.matchedProfile.photos?.[0]?.url || "https://via.placeholder.com/400" }} style={styles.cardImage} />
                <LinearGradient colors={["transparent", "rgba(0,0,0,0.9)"]} style={styles.cardInfoContainer}>
                  <Text style={styles.cardName}>{matchData.matchedProfile.name}</Text>
                  <Text style={{color: '#e5e7eb', fontSize: 16, marginTop: 4}}>{matchData.matchedProfile.bio}</Text>
                  <View style={styles.tagRow}><View style={styles.tag}><Text style={styles.tagText}>ðŸ’¬ Tocar para conversar</Text></View></View>
                </LinearGradient>
              </TouchableOpacity>
            ) : (
              <View style={[styles.card, styles.noMatchCard]}>
                <Text style={{fontSize: 50}}>ðŸ˜´</Text>
                <Text style={{color: 'white', textAlign: 'center', marginTop: 10}}>Sem matches.</Text>
                <TouchableOpacity onPress={() => loadInitialData(userToken!, String(userId))} style={styles.retryBtn}><Text style={{fontWeight: 'bold'}}>Atualizar</Text></TouchableOpacity>
              </View>
            )}
          </ScrollView>
        )}

        {/* ABA CHAT */}
        {tab === "chat" && (
          <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : undefined} style={{flex: 1}}>
            {matchData ? (
              <>
                <View style={styles.chatHeader}>
                  <Image source={{ uri: matchData.matchedProfile.photos?.[0]?.url || "https://via.placeholder.com/50" }} style={styles.chatAvatar} />
                  <Text style={styles.chatName}>{matchData.matchedProfile.name}</Text>
                </View>
                <ScrollView style={styles.chatBody} ref={scrollViewRef}>
                  {messages.map((msg) => (
                    <View key={msg.id} style={[styles.messageRow, { justifyContent: msg.senderId === userId ? "flex-end" : "flex-start" }]}>
                      <View style={[styles.messageBubble, msg.senderId === userId ? styles.messageSent : styles.messageReceived]}>
                        <Text style={{color: msg.senderId === userId ? '#fff' : '#374151'}}>{msg.text}</Text>
                      </View>
                    </View>
                  ))}
                  <View style={{height: 20}} />
                </ScrollView>
                <View style={styles.chatInputContainer}>
                  <TextInput placeholder="Escreva..." style={styles.chatInput} value={newMessage} onChangeText={setNewMessage} placeholderTextColor="#6b7280" />
                  <TouchableOpacity style={styles.sendButton} onPress={handleSendMessage}><Text style={styles.sendButtonText}>âž¤</Text></TouchableOpacity>
                </View>
              </>
            ) : <View style={styles.center}><Text style={{color: '#6b7280'}}>Encontre um match primeiro.</Text></View>}
          </KeyboardAvoidingView>
        )}

        {/* ABA PERFIL */}
        {tab === "profile" && (
          <ScrollView style={styles.page}>
            <Text style={styles.profileTitle}>Meu Perfil</Text>
            {myProfile ? (
              <View style={styles.profileHeader}>
                <Image source={{ uri: myProfile.photos?.[0]?.url || "https://github.com/github.png" }} style={styles.profileAvatar} />
                <Text style={styles.profileName}>{myProfile.name}</Text>
                <Text style={{color: '#6b7280', marginTop: 8, textAlign: 'center'}}>{myProfile.bio || "Sem biografia."}</Text>
                <Text style={{color: '#4f46e5', marginTop: 4, fontWeight: '600'}}>ðŸ“ {myProfile.lastLocation || "LocalizaÃ§Ã£o nÃ£o definida"}</Text>
                <View style={styles.profileButtonRow}>
                  <TouchableOpacity style={styles.editBtn} onPress={openEditModal}>
                    <Text style={styles.editBtnText}>âœï¸ Editar Perfil & Fotos</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ) : <Text style={{textAlign: 'center'}}>Carregando...</Text>}
            <TouchableOpacity style={styles.logoutBtn} onPress={handleLogout}><Text style={styles.logoutBtnText}>Sair</Text></TouchableOpacity>
          </ScrollView>
        )}
      </View>

      {/* --- MODAL ESTILO BOTTOM SHEET --- */}
      <Modal animationType="slide" transparent={true} visible={isEditing} onRequestClose={() => setIsEditing(false)}>
        <View style={styles.modalContainer}>
          {/* Fundo escuro que fecha o modal ao tocar */}
          <TouchableOpacity style={styles.modalOverlay} onPress={() => setIsEditing(false)} />

          {/* ConteÃºdo deslizando de baixo */}
          <View style={styles.modalContent}>
            <View style={styles.modalHeaderHandle} />
            <Text style={styles.modalTitle}>Editar Perfil</Text>

            <ScrollView contentContainerStyle={{paddingBottom: 40}}>
              {/* GALERIA */}
              <Text style={styles.label}>Minhas Fotos</Text>
              <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.galleryScroll}>
                {myProfile?.photos?.map((photo: any) => (
                  <View key={photo.id} style={styles.galleryItem}>
                    <Image source={{ uri: photo.url }} style={styles.galleryImage} />
                    <TouchableOpacity style={styles.deletePhotoBtn} onPress={() => handleDeletePhoto(photo.id)}>
                      <Text style={styles.deletePhotoText}>âœ•</Text>
                    </TouchableOpacity>
                  </View>
                ))}
                {/* BotÃ£o de Adicionar na lista */}
                <TouchableOpacity style={styles.addPhotoSquare} onPress={pickImage}>
                  <Text style={{fontSize: 24, color: '#4f46e5'}}>+</Text>
                </TouchableOpacity>
              </ScrollView>

              {/* PRÃ‰VIA E UPLOAD */}
              {selectedImage && (
                <View style={styles.uploadPreviewContainer}>
                  <Image source={{ uri: selectedImage.uri }} style={styles.uploadPreviewImage} />
                  <View style={{flex: 1}}>
                    <Text style={{fontSize: 14, fontWeight: 'bold'}}>Nova foto selecionada</Text>
                    <TouchableOpacity 
                      style={[styles.uploadBtn, uploadingPhoto && {opacity: 0.5}]} 
                      onPress={handleUploadPhoto}
                      disabled={uploadingPhoto}
                    >
                      {uploadingPhoto ? <ActivityIndicator color="#fff" /> : <Text style={{color:'#fff', fontWeight:'bold'}}>Enviar Foto â¬†ï¸</Text>}
                    </TouchableOpacity>
                  </View>
                  <TouchableOpacity onPress={() => setSelectedImage(null)} style={{padding: 10}}>
                    <Text style={{color: 'red', fontSize: 20}}>âœ•</Text>
                  </TouchableOpacity>
                </View>
              )}

              {/* CAMPOS DE TEXTO */}
              <Text style={styles.label}>Nome</Text>
